        <button
          className='btn btn-outline-secondary btn-sm'
          disabled={!this.state.enabled}
          onClick={this.resetChart}
        >
          Remove data plot click event
        </button>
      </div>
    )
  }
}

ReactDOM.render(
  <Chart />,
  document.getElementById('root'),
